}else if(myArrayString===null){
	//print("myString = null");			
	bReturnControl = false;
}else{

		//Merke: Versuche so ein 'echtes' Array zu machen, sonst geht man Buchstabe für Buchstabe vor.
		var objectArray = myArrayString.split(',');  

