	//Merke: Damit wird auch unter Java 8 (Nashorn) die alte Java 7 (Rhino) Funktionalität bereitgestellt.
	try{
		load("nashorn:mozilla_compat.js"); //Das wirft ausser in Java 8 einen Fehler.
	}catch(e){}
}









