		myArrayReturn[0]="<#!!FGLUNDEFINED!!#>";
	}
	if(mySize>=1){
		//Analog zu LSZ: ZCraft.Logic.Formula32  wie <#!!FGLSLASH!!#> <#!!FGLDBLSLASH!!#>
		for(icount=0; icount<=mySize-1; icount++){
			myArrayReturn[icount]="<#!!FGLEMPTY!!#>";
		}												
	}								
}else{
	//print("KEINE Zahl");
	var myArrayReturn = new Array();

	//Analog zu LSZ: ZCraft.Logic.Formula32  wie <#!!FGLSLASH!!#> <#!!FGLDBLSLASH!!#>
	myArrayReturn[0]="<#!!FGLUNDEFINED!!#>";
}

