	//äquivalent zu JSZ - Kernel
	//@LeftBack equivalent, !!! casesensitive
	function getStrLeftBackStr(sourceStr, keyStr){
		arr = sourceStr.split(keyStr)
		arr.pop();
		return (keyStr==null | keyStr=='') ? '' : arr.join(keyStr)
	}







